                                                    ),
                                                    svg.prop("dur", "2s"),
                                                    svg.prop(
                                                        "repeatCount",
                                                        "indefinite"
                                                    )
                                                )
                                            )
                                        ),
                                        svg.el(
                                            "stop",
                                            string.concat(
                                                svg.prop("offset", "100%"),
                                                svg.prop(
                                                    "style",
                                                    "stop-color:#3333ff;stop-opacity:1"
                                                )
                                            ),
                                            svg.el(
                                                "animate",
                                                string.concat(
                                                    svg.prop(
                                                        "attributeName",
                                                        "stop-color"
                                                    ),
                                                    svg.prop(
                                                        "values",
                                                        "#3333ff;#ff00cc;#3333ff"
                                                    ),
                                                    svg.prop("dur", "2s"),
                                                    svg.prop(
                                                        "repeatCount",
                                                        "indefinite"
                                                    )
                                                )
                                            )
                                        )
                                    )
                                ),
                                svg.linearGradient(
                                    string.concat(
                                        svg.prop("id", "background"),
                                        svg.prop("x1", "0%"),
                                        svg.prop("x2", "0%"),
                                        svg.prop("y1", "0%"),
                                        svg.prop("y2", "100%")
                                    ),
                                    string.concat(
                                        svg.el(
                                            "stop",
                                            string.concat(
                                                svg.prop("offset", "0%"),
