pragma solidity ^0.8.4;

import {LibString} from "solady/src/utils/LibString.sol";

/**
 * @title JSON
 * @author emo.eth
 * @notice TODO: overrides for common types that automatically stringify
 */
library json {
    string private constant NULL = "";

    using LibString for string;

    /**
     * @notice enclose a string in {braces}
     *         Note: does not escape quotes in value
     * @param  value string to enclose in braces
     * @return string of {value}
     */
    function object(string memory value) internal pure returns (string memory) {
        return string.concat("{", value, "}");
    }

    /**
     * @notice enclose a string in [brackets]
     *         Note: does not escape quotes in value
     * @param value string to enclose in brackets
     * @return string of [value]
     */
    function array(string memory value) internal pure returns (string memory) {
        return string.concat("[", value, "]");
    }

    /**
     * @notice enclose name and value with quotes, and place a colon "between":"them".
     *         Note: escapes quotes in name and value
     * @param name name of property
     * @param value value of property
     * @return string of "name":"value"
     */
    function property(
        string memory name,
        string memory value
    ) internal pure returns (string memory) {
        return
            string.concat(
                '"',
                name.escapeJSON(),
                '":"',
                value.escapeJSON(),
                '"'
            );
    }

    /**
     * @notice enclose name with quotes, but not rawValue, and place a colon "between":them
     *         Note: escapes quotes in name, but not value (which may itself be a JSON object, array, etc)
     * @param name name of property
     * @param rawValue raw value of property, which will not be enclosed in quotes
     * @return string of "name":value
     */
    function rawProperty(
        string memory name,
        string memory rawValue
    ) internal pure returns (string memory) {
        return string.concat('"', name.escapeJSON(), '":', rawValue);
    }

    /**
     * @notice comma-join an array of properties and {"enclose":"them","in":"braces"}
     *         Note: does not escape quotes in properties, as it assumes they are already escaped
     * @param properties array of '"name":"value"' properties to join
     * @return string of {"name":"value","name":"value",...}
     */
    function objectOf(
        string[] memory properties
    ) internal pure returns (string memory) {
        return object(_commaJoin(properties));
    }

    /**
     * @notice comma-join an array of values and enclose them [in,brackets]
     *         Note: does not escape quotes in values, as it assumes they are already escaped
     * @param values array of values to join
     * @return string of [value,value,...]
     */
    function arrayOf(
        string[] memory values
    ) internal pure returns (string memory) {
        return array(_commaJoin(values));
    }

    /**
     * @notice comma-join two arrays of values and [enclose,them,in,brackets]
     *         Note: does not escape quotes in values, as it assumes they are already escaped
     * @param values1 first array of values to join
     * @param values2 second array of values to join
     * @return string of [values1_0,values1_1,values2_0,values2_1...]
     */
    function arrayOf(
        string[] memory values1,
        string[] memory values2
    ) internal pure returns (string memory) {
        if (values1.length == 0) {
            return arrayOf(values2);
        } else if (values2.length == 0) {
            return arrayOf(values1);
        }
        return
            array(string.concat(_commaJoin(values1), ",", _commaJoin(values2)));
    }

    /**
     * @notice enclose a string in double "quotes", escaping any existing quotes
     * @param str string to enclose in quotes
     * @return string of "value"
     */
    function quote(string memory str) internal pure returns (string memory) {
        return string.concat('"', str.escapeJSON(), '"');
    }

    /**
     * @notice enclose each string in an array in double "quotes", escaping any existing quotes
     * @param strs array of strings, each to escape and enclose in quotes
     */
    function quote(
        string[] memory strs
    ) internal pure returns (string[] memory) {
        string[] memory result = new string[](strs.length);
        for (uint256 i = 0; i < strs.length; ) {
            result[i] = quote(strs[i]);
            unchecked {
                ++i;
            }
        }
        return result;
    }

    /**
     * @notice comma-join an array of strings
     * @param values array of strings to join
     * @return string of value,value,...
     */
    function _commaJoin(
        string[] memory values
    ) internal pure returns (string memory) {
        return _join(values, ",");
    }

    /**
     * @notice join two strings with a comma
     * @param value1 first string
     * @param value2 second string
     * @return string of value1,value2
     */
    function _commaJoin(
        string memory value1,
        string memory value2
    ) internal pure returns (string memory) {
        return string.concat(value1, ",", value2);
    }

    /**
     * @notice join an array of strings with a specified separator
     * @param values array of strings to join
     * @param separator separator to join with
     * @return string of value<separator>value<separator>...
     */
    function _join(
        string[] memory values,
        string memory separator
    ) internal pure returns (string memory) {
        if (values.length == 0) {
            return NULL;
        }
        string memory result = values[0];
        for (uint256 i = 1; i < values.length; ++i) {
            result = string.concat(result, separator, values[i]);
        }
        return result;
    }
}
","keccak256":"0x43bcc48f311fa488709bfd3342c231fbadb97a88d436a176d5395e14ac9b2790","license":"MIT"},"contracts/shared/libraries/svg.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import {LibString} from "solady/src/utils/LibString.sol";

library svg {
    using LibString for uint256;

    // used to simulate empty strings
    string internal constant NULL = "";

    function top(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return svg_(true, props, children);
    }

    function svg_(
        bool includeXmlns,
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        if (includeXmlns) {
            return
                el(
                    "svg",
                    string.concat('xmlns="http://www.w3.org/2000/svg" ', props),
                    children
                );
        } else {
            return el("svg", props, children);
        }
    }

    function g(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("g", props, children);
    }

    function path(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("path", props, children);
    }

    function text(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("text", props, children);
    }

    function line(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("line", props, children);
    }

    function circle(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("circle", props, children);
    }

    function circle(string memory props) internal pure returns (string memory) {
        return el("circle", props);
    }

    function rect(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("rect", props, children);
    }

    function rect(string memory props) internal pure returns (string memory) {
        return el("rect", props);
    }

    function filter(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("filter", props, children);
    }

    function cdata(
        string memory content
    ) internal pure returns (string memory) {
        return string.concat("<![CDATA[", content, "]]>");
    }

    /* GRADIENTS */
    function radialGradient(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("radialGradient", props, children);
    }

    function linearGradient(
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return el("linearGradient", props, children);
    }

    function gradientStop(
        uint256 offset,
        string memory stopColor,
        string memory props
    ) internal pure returns (string memory) {
        return
            el(
                "stop",
                string.concat(
                    prop("stop-color", stopColor),
                    prop("offset", string.concat(offset.toString(), "%")),
                    props
                )
            );
    }

    function animateTransform(
        string memory props
    ) internal pure returns (string memory) {
        return el("animateTransform", props);
    }

    function image(
        string memory href,
        string memory props
    ) internal pure returns (string memory) {
        return el("image", string.concat(prop("href", href), props));
    }

    /* COMMON */
    // A generic element, can be used to construct any SVG (or HTML) element
    function el(
        string memory tag,
        string memory props,
        string memory children
    ) internal pure returns (string memory) {
        return
            string.concat("<", tag, " ", props, ">", children, "</", tag, ">");
    }

    // A generic element, can be used to construct any SVG (or HTML) element without children
    function el(
        string memory tag,
        string memory props
    ) internal pure returns (string memory) {
        return string.concat("<", tag, " ", props, "/>");
    }

    // an SVG attribute
    function prop(
        string memory key,
        string memory val
    ) internal pure returns (string memory) {
        return string.concat(key, "=", '"', val, '" ');
    }

    // formats a CSS variable line. includes a semicolon for formatting.
    function setCssVar(
        string memory key,
        string memory val
    ) internal pure returns (string memory) {
        return string.concat("--", key, ":", val, ";");
    }

    // formats getting a css variable
    function getCssVar(
        string memory key
    ) internal pure returns (string memory) {
        return string.concat("var(--", key, ")");
    }

    // formats getting a def URL
    function getDefURL(string memory id) internal pure returns (string memory) {
        return string.concat("url(#", id, ")");
    }

    // formats generic rgba color in css
    function rgba(
        uint256 r,
        uint256 _g,
        uint256 b,
        uint256 a
    ) internal pure returns (string memory) {
        string memory formattedA = a < 100
            ? string.concat("0.", a.toString())
            : "1";
        return
            string.concat(
                "rgba(",
                r.toString(),
                ",",
                _g.toString(),
                ",",
                b.toString(),
                ",",
                formattedA,
                ")"
            );
    }
}
